const tabs = document.querySelectorAll(".tabs > div"),
  tabs_arr = Array.from(tabs),
  tab_content = document.querySelectorAll(".tab-content > div");
for (let i = 0; i < tabs.length; i++) {
  let t = tabs[i];
  t.addEventListener("click", (t) => {
    if (t.target.classList.contains("active")) return;
    let e = t.target.parentElement?.querySelector(".active");
    if (e) {
      e.classList.remove("active");
      let a = tabs_arr.indexOf(e);
      tab_content[a].classList.add("hide");
    }
    t.target.classList.add("active"), tab_content[i].classList.remove("hide");
  });
}
